import groovy.json.*

// get the response from the first request using its name
def response = context.expand('${AddPlace#Response}')
// parse it
def json = new JsonSlurper().parseText(response)
log.info json.place_id
// get the json value an set it as property in testCase
context.testCase.setPropertyValue("place_id",json.place_id.toString())
